var OSIDS=[0,0,0,0];function showItems(e){let t=jQuery("input[name=os]:checked").attr("id");void 0===t&&(t="os_1_0");let n=t.split("_");switch(OSIDS[n[1]]=n[2],e){case 0:document.getElementById("section_1_items").style.display="none",document.getElementById("section_3_items").style.display="none";break;case 1:document.getElementById("os_1_"+OSIDS[1]).checked="checked",document.getElementById("section_1_items").style.display="block",document.getElementById("section_3_items").style.display="none";break;case 2:document.getElementById("os_2_"+OSIDS[2]).checked="checked",document.getElementById("section_1_items").style.display="none",document.getElementById("section_3_items").style.display="none";break;case 3:document.getElementById("os_3_"+OSIDS[3]).checked="checked",document.getElementById("section_1_items").style.display="none",document.getElementById("section_3_items").style.display="block"}}function showItemsResult(){switch(parseInt(document.getElementById("section").value)){case 0:document.getElementById("section-wrapper").style.display="none",document.getElementById("section_1_items").style.display="none",document.getElementById("section_3_items").style.display="none";break;case 1:document.getElementById("section-wrapper").style.display="flex",document.getElementById("section_1_items").style.display="block",document.getElementById("section_3_items").style.display="none";break;case 2:document.getElementById("section-wrapper").style.display="flex",document.getElementById("section_1_items").style.display="none",document.getElementById("section_3_items").style.display="none";break;case 3:document.getElementById("section-wrapper").style.display="flex",document.getElementById("section_1_items").style.display="none",document.getElementById("section_3_items").style.display="block"}}function submitForm(){let e=parseInt(document.getElementById("section").value);e>0&&(document.getElementById("os").value=document.getElementById("os_"+e).value)}